INSERT INTO ask_question VALUES ('3979','130','128','130','0','0','mengmakies','13707','谁能解释一下这段Three.js代码？','<pre class=\"brush:js;toolbar:false;\">// Three.js提供多种渲染方式，可以用Canvas、WebGL(硬件加速渲染)、SVG渲染\r\n             \r\n            // 一个3D世界包含以下东西：\r\n            // 场景、渲染器、摄像机、对象\r\n         \r\n            // 设置场景大小\r\n            var WIDTH = document.documentElement.offsetWidth || 800;\r\n            var HEIGHT = document.documentElement.clientHeight || 600;\r\n             \r\n            // 设置摄像机的属性()\r\n            var VIEW_ANGLE = 45;// 视野角度\r\n            var ASPECT = WIDTH / HEIGHT;\r\n            var NEAR = 1;// 近裁剪距离\r\n            var FAR = 10000;// 远剪裁距离\r\n             \r\n            // 创建场景\r\n            var scene = new THREE.Scene();\r\n             \r\n            // 创建渲染器\r\n            var renderer = new THREE.SVGRenderer();\r\n            renderer.setSize(WIDTH, HEIGHT);// 容器图像的大小\r\n            document.body.appendChild(renderer.domElement);// 创建容器，绑定渲染器，用于显示图形\r\n             \r\n            // 创建摄像机\r\n            var camera = new THREE.PerspectiveCamera(VIEW_ANGLE, ASPECT, NEAR, FAR);\r\n            camera.position.set(0,0,300);\r\n            scene.add(camera);\r\n             \r\n            // 显示对象\r\n            var geometry = new THREE.CubeGeometry(100, 100, 100);// 几何属性\r\n            var material = new THREE.MeshLambertMaterial({color:0xFFF000});// material:THREE.Material实例，物体的材质，包括颜色、透明度、反光率等属性\r\n             \r\n            var cube = new THREE.Mesh(geometry, material);\r\n            cube.rotation.set(10,20,10);// 放置角度\r\n             \r\n            scene.add(cube);\r\n             \r\n            // 灯光\r\n            var light = new THREE.DirectionalLight(0xFFFFFF);// 直线光\r\n            light.position.set(25, 50, 130).normalize();\r\n            scene.add(light);\r\n \r\n            // 动画\r\n            (function anime(){\r\n                cube.rotation.x += 0.1;// 改变立方体角度\r\n                renderer.render(scene, camera);// 开始渲染\r\n                return requestAnimationFrame(anime);\r\n            })()</pre><p>&nbsp;</p>','','1','1331012053','1336196053','0','0','0','9','','','383');
