INSERT INTO ask_answer VALUES ('1415','4033','2.0版本计划什么时候发布？','江南孤鹜','13683','1331872098','0','<p><span style=\"white-space:nowrap;\">var&nbsp;LockService&nbsp;=&nbsp;function&nbsp;()&nbsp;{&nbsp;};</span></p><p><span style=\"white-space:nowrap;\"><br /></span></p><p><span style=\"white-space:nowrap;\">//&nbsp;根据身份证号判断是否锁定该项目</span></p><p><span style=\"white-space:nowrap;\">LockService.CheckIsLockedByPerson&nbsp;=&nbsp;function&nbsp;(business,personInfo)&nbsp;{</span></p><p><span style=\"white-space:nowrap;\"><br /></span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;判断是否锁定该项目</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;$.ajax({</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;type:&nbsp;&quot;get&quot;,</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dataType:&nbsp;&quot;text&quot;,</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;url:&nbsp;&quot;Handler/CheckIsLocked.ashx&quot;,</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cache:&nbsp;false,</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;data:&nbsp;{&nbsp;Action:&nbsp;&quot;CheckIsLockedByPerson&quot;,&nbsp;Business:business,&nbsp;Person:&nbsp;personInfo&nbsp;},</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;async:&nbsp;true,</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;beforeSend:&nbsp;function&nbsp;()&nbsp;{</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;success:&nbsp;function&nbsp;(msg)&nbsp;{</span></p><p><span style=\"white-space:nowrap;\">&nbsp;&nbsp;&nbsp;&nbsp